import styled from 'styled-components';

export const LanguageChoiceContainer = styled.div`
  width: 12vw;
  display: flex;
  justify-content: space-between;
  align-items: center;
  line-height: 0.2vmin;
  overflow: hidden;

  button {
    padding: 5px 1vmin;
    margin: 1vmin;
    border-radius: 8px;
    cursor: pointer;
    z-index: 2;
  }

  button:focus {
    /* outline: 0; */
  }
`;
