import styled from 'styled-components';

export const LanguageChoiceContainer = styled.div`
  width: 70%;
  display: flex;
  justify-content: space-between;
  align-items: center;

  button {
    padding: 5px 10px;
    border-radius: 8px;
  }

  button:focus {
    outline: 0;
    background: black;
    opacity: 0.4;
  }

  .pressed {
    box-shadow: inset 0px 0px 5px #c1c1c1;
    outline: none;
    background: #e5e5e5;
  }
`;
