import styled, { keyframes } from 'styled-components';

const rotate = keyframes`
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(180deg)
  }
`;

const expand = keyframes`
  from {
    width: 10%;
  }
  to {
    width: 50%;
  }
`;

export const HeaderContainer = styled.div`
  height: 10vh;
  display: flex;
  justify-content: space-around;
  align-items: center;
`;

export const InfoContainer = styled.div`
  width: 10%;
  display: flex;
  justify-content: flex-end;

  img {
    width: 50px;
  }

  &:hover {
    animation: ${expand} 0.3s linear forwards;

    &::before {
      content: "Choose your input and ouput languages then toggle the microphone to start/stop real time translation.";
      width: 30vw;
      display: block;
      position: relative;
      margin: auto;

      animation: ${rotate} 3s linear forwards;
    }

    img {
      animation: ${rotate} 0.5s ease-in-out forwards;
    }
  }
`;
