import styled from 'styled-components';

export const LanguageToggleContainer = styled.div`
  height: 90%;
  width: 15vw;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  align-items: center;
  border: white solid 4px;
  border-radius: 6px;
  color: whitesmoke;
  font-weight: 550;

  h3 {
    margin: 2px;
    font-size: 3vmin;
  }
`;
