import React from 'react';

import LanguageButton from '../LanguageButton/LanguageButton';

import {
  LanguageChoiceContainer
} from './LanguageChoiceStyles';

const LanguageChoice = ({ language, languageCode, handler }) => (
  <LanguageChoiceContainer>
    {/* <LanguageButton languageCode={languageCode} handler={handler}/> */}
    <button onClick={(event) => handler(languageCode, event)}>{languageCode.slice(0, 2)}</button>
    <h5>{language}</h5>
  </LanguageChoiceContainer>
)

export default LanguageChoice;
