import React, { useState, useEffect } from 'react';

import Header from '../../components/Header/Header';
import Interface from '../../components/Interface/Interface';
import Output from '../../components/Output/Output';
import LanguageToggle from '../../components/LanguageToggle/LanguageToggle';
import MicOn from '../../components/MicOn/MicOn';
import MicOff from '../../components/MicOff/MicOff';

import {
  MainPageContainer
} from './MainPageStyles';



function MainPage() {
  //state
  const [ text, setText ] = useState('');
  const [ translatedText, setTranslatedText ] = useState('');
  const [ inputLanguage, setInputLanguage ] = useState('en-US');
  const [ outputLanguage, setOutputLanguage ] = useState('es');

  //fetch this
  const IAM_access_token = 'eyJraWQiOiIyMDE5MDcyNCIsImFsZyI6IlJTMjU2In0..G_VtmSBXj4zfOIQ70MdshefS_8mg2tB7qR6TvAunka5ZEnlQfy1tF9i60v0o92d_riJQo_m-LcChhWU8mzenkya3nbsbkEg5Iq9DKe8-uIDxOTnxTlDKMYjZNwZe36YB58g8JWDJkQXr2Z8mJevQp7DK6tMFwSEluPFHYKCFd6AqXiQHrUaWykKOzyOsDPC1PZqInP8rS3_EvVcPh37kJDjCNqhP207J8cYJpSgK3PmCUJEcoWIyf1M_hH__ZmITH9JKclLOCRO9p3y2t02jp4ww5JBulvimAqlWV9_sbiKRgPS2dSesaVR0dWdVfZqxPUESfMMz7YL3LcNKxIc1AQ'

  const wsURI = `wss://stream.watsonplatform.net/speech-to-text/api/v1/recognize?access_token=${IAM_access_token}&model=${inputLanguage}_BroadbandModel`;

  const websocket = new WebSocket(wsURI);
  websocket.onopen = function(event) { onOpen(event) };
  websocket.onclose = function(event) { onClose(event) };
  websocket.onmessage = function(event) { onMessage(event) };
  websocket.onerror = function(event) { onError(event) };

  function onOpen(evt) {
    console.log('connecting')
  }

  function onClose(event) {
    console.log('closing:', event.data);
  }

  function onMessage(event) {
    const data = JSON.parse(event.data);
    if (data.hasOwnProperty('results')) {
      if (data.results.length) {
      console.log(data)
      fetch('http://localhost:3000/', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          data: data.results[0].alternatives[0].transcript,
          outputLanguage,
          inputLanguage
        })
      }).then(response => response.json())
      .then(response => {
        setText(data.results[0].alternatives[0].transcript)
        setTranslatedText(response)
      })
    }}
  }

  function onError(event) {
    console.log(event.data);
  }

  const inputLanguageHandler = (language, event) => {
    // event.preventDefault()
    if (outputLanguage === language.slice(0, 2)) {
      setOutputLanguage(inputLanguage.slice(0, 2))
    }
    setInputLanguage(language);
  }
  const outputLanguageHandler = (language, event) => {
    if (inputLanguage === language) {
      switch (outputLanguage) {
        case 'en':
          setInputLanguage('en-US')
          break;
        case 'fr':
          setInputLanguage('fr-FR')
          break;
        case 'es':
          setInputLanguage('es-ES')
          break;
        default:
          break;
      }
    }
    setOutputLanguage(language.slice(0, 2));
  }

  let listen;
  let stop;

  useEffect(() => {

    // const listen = document.querySelector('.startRecording');
    // const stop = document.querySelector('.stopRecording');

    if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
      console.log('getUserMedia supported.');
      navigator.mediaDevices.getUserMedia (
        // constraints - only audio needed for this app
        { audio: true, sampleSize: 16 })

        // Success callback
        .then(function(stream) {
            const mediaRecorder = new MediaRecorder(stream);
            listen = function(event) {
              event.preventDefault();
              console.log('inputLang:', inputLanguage)
              console.log('outputLang:', outputLanguage)
              console.log('uri:', wsURI)
              if (mediaRecorder.state === 'inactive') {
                websocket.send(JSON.stringify({
                  action: 'start',
                  'content-type': 'application/octet-stream',
                  "interim_results": true
                }));
                mediaRecorder.start(1000);
                console.log(mediaRecorder.state);
                console.log("recorder started");
              }

              stop = function(event) {
                event.preventDefault();
                if(mediaRecorder.state === 'recording') {
                  mediaRecorder.stop();
                  console.log(mediaRecorder.state);
                  console.log("recorder stopped");
                }
              }

              mediaRecorder.ondataavailable = function(event) {
                websocket.send(event.data)
              }

              mediaRecorder.onstop = function(event) {            
                websocket.send(JSON.stringify({action: 'stop'}))
              }
        }})
        .catch(function(err) {
            console.log('The following getUserMedia error occured: ' + err);
        }
      );
  } else {
      console.log('getUserMedia not supported on your browser!');
  }
}, [inputLanguage, websocket, outputLanguage, wsURI])

  return (  
    <MainPageContainer>
      <Header />
      <Interface>
        <LanguageToggle heading="From" handler={inputLanguageHandler} />
        {/* <img src="./images/micOff.png" alt="mic symbol to stop recording" className="stopRecording"/>
        <img src="./images/micOn.png" alt="mic symbol to start recording" className="startRecording"/> */}
        <MicOn onClick={listen}/>
        <MicOff onClick={stop}/>
        <LanguageToggle heading="To" handler={outputLanguageHandler}/>
      </Interface>
      <Output originalText={text} translatedText={translatedText}/>
    </MainPageContainer>
  )
}

export default MainPage;
