import React, { useState } from 'react';

import LanguageChoice from '../LanguageChoice/LanguageChoice';

import {
  LanguageToggleContainer
} from './LanguageToggleStyles';

const LanguageToggle = ({ heading, handler }) => {
  const [ pressed, setPressed ] = useState(1);

  const pressHandler = id => setPressed(id);

  return (
    <LanguageToggleContainer>
      <h3>{heading}</h3>
      <LanguageChoice id={1} language="English" languageCode="en-US" handlers={handler, pressHandler}/>
      <LanguageChoice language="Français" languageCode="fr-FR" handlers={handler, pressHandler}/>
      <LanguageChoice language="Español" languageCode="es-ES" handlers={handler, pressHandler}/>
    </LanguageToggleContainer>
  )
}

export default LanguageToggle;
